import { Link } from "react-router-dom";
import { useState } from "react";
import { useForm } from "react-hook-form";

const labelData = [
  {
    label: "Name",
    name: "username",
    type: "text",
    placeholder: "Enter your name",
  },
  {
    label: "Email",
    name: "email",
    type: "email",
    placeholder: "Enter your email",
  },
  {
    label: "Role",
    name: "role",
    type: "text",
    placeholder: "Enter your role",
  },
  {
    label: "Password",
    name: "password",
    type: "password",
    placeholder: "Enter your password",
  },
];
const Registration = () => {
  const {
    register,
    handleSubmit,
    formState: { error },
  } = useForm();

  const [inputs, setInputs] = useState({
    username: "",
    email: "",
    role: "",
    password: "",
  });

  const handleRegistration = (data) => {
    console.log("handleRegistrationdata:", data);
  };
  const inputsHandler = (e) => {
    const name = e.target.name;
    const value = e.target.value;

    setInputs({ ...inputs, [name]: value });

    // console.log("name:", name, value);
  };

  return (
    <>
      <section>
        <div className="container-fluid h-custom">
          <div className="row d-flex justify-content-center align-items-center h-100 mt-lg-5">
            <div className="col-md-9 col-lg-6 col-xl-5">
              <img
                src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
                className="img-fluid"
                alt="Sample image"
              />
            </div>
            <div className="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
              <form onSubmit={handleSubmit(handleRegistration)}>
                <div className="d-flex flex-row align-items-center justify-content-center justify-content-lg-start mb-5">
                  <h1
                    className="lead fw-normal mb-0 me-3"
                    style={{ fontSize: "2rem" }}
                  >
                    Sign Up
                  </h1>
                </div>

                {/* <!-- input --> */}
                {labelData.map((data, index) => {
                  {
                    console.log("data", data.name);
                  }
                  return (
                    <>
                      <div
                        data-mdb-input-init
                        className="form-outline mb-4"
                        key={index}
                      >
                        <label className="form-label">{data.lable}</label>
                        <input
                          type={data.type}
                          name={data.name}
                          id="form3Example3"
                          className="form-control form-control-lg"
                          placeholder={data.placeholder}
                          //   value={inputs[data.name]}
                          {...register(data.name)}
                          onChange={inputsHandler}
                        />
                      </div>
                    </>
                  );
                })}

                <div className="d-flex justify-content-between align-items-center">
                  {/* <!-- Checkbox --> */}
                  <div className="form-check mb-0">
                    <input
                      className="form-check-input me-2"
                      type="checkbox"
                      value=""
                      id="form2Example3"
                    />
                    <label className="form-check-label" for="form2Example3">
                      Remember me
                    </label>
                  </div>
                  <a href="#!" className="text-body">
                    Forgot password?
                  </a>
                </div>

                <div className="text-center text-lg-start mt-4 pt-2">
                  <button
                    type="button"
                    data-mdb-button-init
                    data-mdb-ripple-init
                    className="btn btn-primary btn-lg"
                    style={{ paddingLeft: "2.5rem", paddingRight: "2.5rem" }}
                  >
                    Sign Up
                  </button>
                  <p className="small fw-bold mt-2 pt-1 mb-0">
                    If have an account?
                    <Link to="/login" className="link-danger">
                      LOGIN
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Registration;
