import { Link } from "react-router-dom";
import { useState } from "react";
import { useForm } from "react-hook-form";

const lableData = [
  {
    label: "Name", // Corrected property name
    name: "username",
    type: "text",
    placeholder: "Enter your name",
  },
  {
    label: "Email", // Corrected property name
    name: "email",
    type: "email",
    placeholder: "Enter your email",
  },
  {
    label: "Role", // Corrected property name
    name: "role",
    type: "text",
    placeholder: "Enter your role",
  },
  {
    label: "Password", // Corrected property name
    name: "password",
    type: "password",
    placeholder: "Enter your password",
  },
];

const Registration = () => {
  const { register, handleSubmit, reset, formState: { errors } } = useForm();

  const handleRegistration = (data) => {
    console.log("Registration data:", data);
    reset();
  };

  return (
    <>
      <section>
        <div className="container-fluid h-custom">
          <div className="row d-flex justify-content-center align-items-center h-100 mt-lg-5">
            <div className="col-md-9 col-lg-6 col-xl-5">
              <img
                src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
                className="img-fluid"
                alt="Sample image"
              />
            </div>
            <div className="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
              <form onSubmit={handleSubmit(handleRegistration)}>
                <div className="d-flex flex-row align-items-center justify-content-center justify-content-lg-start mb-5">
                  <h1
                    className="lead fw-normal mb-0 me-3"
                    style={{ fontSize: "2rem" }}
                  >
                    Sign Up
                  </h1>
                </div>

                {/* Input Fields */}
                {lableData.map((data, index) => (
                  <div
                    className="form-outline mb-4"
                    key={index}
                  >
                    <label className="form-label">{data.label}</label>
                    <input
                      type={data.type}
                      name={data.name}
                      className="form-control form-control-lg"
                      placeholder={data.placeholder}
                      {...register(data.name)} // Register input with react-hook-form
                    />
                  </div>
                ))}

                <div className="text-center text-lg-start mt-4 pt-2">
                  <button
                    type="submit"
                    className="btn btn-primary btn-lg"
                    style={{ paddingLeft: "2.5rem", paddingRight: "2.5rem" }}
                  >
                    Sign Up
                  </button>
                  <p className="small fw-bold mt-2 pt-1 mb-0">
                    If you have an account?{" "}
                    <Link to="/login" className="link-danger">
                      LOGIN
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Registration;
