import { useState } from "react";
import { Link } from "react-router-dom";

const lableData = [
  {
    lable: "Name",
    name: "username",
    type: "text",
    placeholder: "Enter your name",
  },
  {
    lable: "Password",
    name: "password",
    type: "password",
    placeholder: "Enter your password",
  },
];
const Login = () => {
  const [inputs, setInputs] = useState({
    username: "",
    password: "",
  });

  const inputsHandler = (e) => {
   const name = e.target.name;
   const value = e.target.value;

    setInputs({...inputs, [name]:value});

    // console.log("name:", name, value)
  };

  return (
    <>
      <section class="mt-lg-5">
        <div class="container-fluid h-custom">
          <div class="row d-flex justify-content-center align-items-center h-100">
            <div class="col-md-9 col-lg-6 col-xl-5">
              <img
                src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-login-form/draw2.webp"
                class="img-fluid"
                alt="Sample image"
              />
            </div>
            <div class="col-md-8 col-lg-6 col-xl-4 offset-xl-1">
              <form>
                <div class="d-flex flex-row align-items-center justify-content-center justify-content-lg-start mb-5">
                  <h1 class="lead fw-normal mb-0 me-3" style={{fontSize:"2rem"}}>LOGIN</h1>
                </div>

                {/* <!-- input --> */}
                {lableData.map((data, index) => {
                  {
                    console.log("data", data.name);
                  }
                  return (
                    <>
                      <div
                        data-mdb-input-init
                        class="form-outline mb-4"
                        key={index}
                      >
                        <label class="form-label">{data.lable}</label>
                        <input
                          type={data.type}
                          name={data.name}
                          id="form3Example3"
                          class="form-control form-control-lg"
                          placeholder={data.placeholder}
                          value={inputs[data.name]}
                          onChange={inputsHandler}
                        />
                      </div>
                    </>
                  );
                })}

                <div class="d-flex justify-content-between align-items-center">
                  {/* <!-- Checkbox --> */}
                  <div class="form-check mb-0">
                    <input
                      class="form-check-input me-2"
                      type="checkbox"
                      value=""
                      id="form2Example3"
                    />
                    <label class="form-check-label" for="form2Example3">
                      Remember me
                    </label>
                  </div>
                  <a href="#!" class="text-body">
                    Forgot password?
                  </a>
                </div>

                <div class="text-center text-lg-start mt-4 pt-2">
                <button
                    type="button"
                    data-mdb-button-init
                    data-mdb-ripple-init
                    class="btn btn-primary btn-lg"
                    style={{ paddingLeft: "2.5rem", paddingRight: "2.5rem" }}
                  >
                   LOGIN
                  </button>
                  <p class="small fw-bold mt-2 pt-1 mb-0">
                    Don't have an account?
                    <Link to="/registration" class="link-danger">
                     SIGN UP
                    </Link>
                  </p>
                </div>
              </form>
            </div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Login;
